import React from 'react';

export default function Formulario() {
    function data(event) {
        event.preventDefault();
        const formData = new FormData(event.currentTarget);

        // Obtener el valor del campo de nombre
        const name = formData.get("name");

        // Obtener el valor del campo de teléfono
        const phone = formData.get("phone");

        // Mostrar los valores en la consola o hacer algo con ellos
        console.log(`Nombre: ${name}`);
        console.log(`Teléfono: ${phone}`);

        // Si quieres mostrarlos en un alerta, puedes hacer algo como esto:
        alert(`Nombre: ${name}\nTeléfono: ${phone}`);
    }

    return (
        <div className="container-fluid mt-5">   
            <section>
                <h2>Formulario</h2>
                <form className="card border-primary mb-3" onSubmit={data}>
                    <div className='p-5 '>
                        <div className='form-group '>
                            <label className="col-sm-2 col-form-label" htmlFor='inpNombre'>Nombre:</label>
                            <input id='inpNombre' name="name" type='text' className='form-control' placeholder='Ingrese su nombre' />
                        </div>

                        <div className='form-group'>
                            <label className="col-sm-2 col-form-label" htmlFor="inpTel">Numero de telefono:</label>
                            <input id="inpTel" name="phone" type='number' className='form-control' placeholder='Ingrese su numero de telefono' />
                        </div>

                        <div className='form-group'>
                            <button type="submit" className="btn btn-info">Subir</button>
                        </div>
                    </div>
                </form>
            </section>
        </div>
    );
}
