import axios from 'axios';
import React, { useState } from 'react';
import ButtonDel from './btndel';
import ButtonUp from './btnupd';

export function Rows({ userId, userName, userCel,onDelete,onUpdate }) {
    return (
        <tr>
            <td>{userId}</td>
            <td>{userName}</td>
            <td>{userCel}</td>
            <td>
                <ButtonDel onClick={onDelete}/>
                <br/>
                <br/>
                <ButtonUp onClick={onUpdate}/>
            </td>
        </tr>
    );
}

export default function Table({ rows }) {
    const handleDelete=(useState,userId)=>{ 

        console.log(`usuario con el id '${userId}' eliminado`);
    }

    const handleUpdate=(userId,userCel,userName)=>{ 
        console.log(`id: ${userId} nombre: ${userName} celular: ${userCel}`);
        alert(`usuario actilizado`);
    }

    return (
        <div className="container-fluid p-5 mt-3 w-70">
            <table className="table table-hover">
                <thead className='table-info'>
                    <th scope="col">id</th>
                    <th scope="col">nombre</th>
                    <th scope="col">telefono celular</th>
                    <th></th>
                </thead>
                <tbody className='table-secondary'>
                    {rows.map((row, index) => (
                        <Rows 
                        key={index} 
                        userId={row.userId} 
                        userName={row.userName} 
                        userCel={row.userCel}
                        onDelete={() => handleDelete(
                            row.stateRow,
                            row.userId
                            )} 
                        onUpdate={() => handleUpdate(
                            row.userId,
                            row.userCel,
                            row.userName
                            )}
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
}
