import axios from 'axios';
import React, { useState } from 'react';
import ButtonDel from './btndel';
import ButtonUp from './btnupd';
import Alert from './alertup';
import Swal from 'sweetalert2';


export function Rows({ userId, userName, userCel, onDelete, onUpdate }) {
    return (
        <tr>
            <td>{userId}</td>
            <td>{userName}</td>
            <td>{userCel}</td>
            <td>
                <ButtonDel onClick={onDelete} />
                <br />
                <br />
                <ButtonUp onClick={onUpdate} />
            </td>
        </tr>
    );
}

export default function Table({ rows }) {
    const [alertState, setAlert] = useState(false);
    const [inputName, setInputValue] = useState('');
    const [inputCel, setInputCel] = useState('');
    const handleDelete = (useState, userId) => {
        console.log(`usuario con el id '${userId}' eliminado`);
        Swal.fire({
            title:'Eliminar usuario',
            showConfirmButton:true,
            showCancelButton:true
        });
    }

    const handleUpdate = async () => {
        const { value: formValues } = await Swal.fire({
            title: "Actualizar usuario",
            icon:"info",
            html: `
            <label htmlFor="swal-input1">nuevo nombre:</label>
            <input id="swal-input1" class="swal2-input" type="text">
            <label htmlFor="swal-input2">nuevo numero:</label>
            <input id="swal-input2" class="swal2-input" type="number">
          `,
            focusConfirm: false,
            preConfirm: () => {
                return [
                    document.getElementById("swal-input1").value,
                    document.getElementById("swal-input2").value
                ];
            }
        });

        if (formValues) {

            Swal.fire(JSON.stringify(formValues));
        }

    };
    const filteredUsers =rows.filter(user => user.stateRow === 1);


    return (

        <div className="container-fluid p-5 mt-3 w-70">

            <table className="table table-hover">
                <thead className='table-info'>
                    <tr>
                        <th scope="col">id</th>
                        <th scope="col">nombre</th>
                        <th scope="col">telefono celular</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody className='table-secondary'>
                    {rows.map((row, index) => (
                        <Rows
                            key={index}
                            userId={row.userId}
                            userName={row.userName}
                            userCel={row.userCel}
                            onDelete={() => handleDelete(row.stateRow, row.userId)}
                            onUpdate={() => handleUpdate(row.userId, row.userCel, row.userName)}
                        />
                    ))}
                </tbody>
            </table>

        </div>
    );
}
