import axios from 'axios';
import React, { useState } from 'react';
import ButtonDel from './btndel';
import ButtonUp from './btnupd';
import Alert from './alertup';
import Swal from 'sweetalert2';


export function Rows({ userId, userName, userCel, onDelete, onUpdate }) {
    return (
        <tr>
            <td>{userId}</td>
            <td>{userName}</td>
            <td>{userCel}</td>
            <td>
                <ButtonDel onClick={onDelete} />
                <br />
                <br />
                <ButtonUp onClick={onUpdate} />
            </td>
        </tr>
    );
}

export default function Table({ rows }) {
    const [alertState, setAlert] = useState(false);
    const [inputValue, setInputValue] = useState('');

    const handleDelete = (useState, userId) => {
        console.log(`usuario con el id '${userId}' eliminado`);
    }

    const handleUpdate = () => {
        //console.log(`id: ${userId} nombre: ${userName} celular: ${userCel} ${alertState}`);
        //setAlert(true); // Mostrar el modal
        //console.log(alertState);
            Swal.fire({
            title: 'actualice usuario',
            input: 'text',
            input: 'number',
            inputName,
            inputCel,
            preConfirm: () => {
              setInputValue(Swal.getInput()?.value || '')
            },
          })
    }

    const closeModal = () => {
        setAlert(false);
    }
    return (

        <div className="container-fluid p-5 mt-3 w-70">

            <Alert isOpen={alertState} onClose={closeModal}>
                <h2>Confirmar actualización</h2>
                <p>¿Estás seguro de que deseas actualizar?</p>
                <div>
                    <button onClick={closeModal}>Cancelar</button>
                    <button>Confirmar</button>
                </div>
            </Alert>
            <table className="table table-hover">
                <thead className='table-info'>
                    <tr>
                        <th scope="col">id</th>
                        <th scope="col">nombre</th>
                        <th scope="col">telefono celular</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody className='table-secondary'>
                    {rows.map((row, index) => (
                        <Rows
                            key={index}
                            userId={row.userId}
                            userName={row.userName}
                            userCel={row.userCel}
                            onDelete={() => handleDelete(row.stateRow, row.userId)}
                            onUpdate={() => handleUpdate(row.userId, row.userCel, row.userName)}
                        />
                    ))}
                </tbody>
            </table>
            <p>{inputValue}</p>
        </div>
    );
}
