import axios from 'axios';
import React, { useState } from 'react';

export default function Formulario({ onFormSubmit }) {
    const [nombre, setNombre] = useState('');
    const [telefono, setTelefono] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Realizar la solicitud POST con Axios
        try {
            const response = await axios.post('Aun no tengo la ruta  ', {
                userName: nombre,
                phone: telefono
            });

            // Llamar a la función proporcionada por el padre para manejar el envío del formulario
            onFormSubmit(nombre, telefono);

            // Manejar la respuesta según sea necesario
            console.log('Respuesta del servidor:', response.data);
        } catch (error) {
            // Manejar errores de la solicitud
            console.error('Error al enviar el formulario:', error);
        }
    };

    return (
        <div className="container-fluid mt-5">   
            <section>
                <h2>Formulario</h2>
                <form className="card border-primary mb-3" onSubmit={handleSubmit}>
                    <div className='p-5 '>
                        <div className='form-group '>
                            <label className="col-sm-2 col-form-label" htmlFor='inpNombre'>Nombre:</label>
                            <input 
                                required
                                id='inpNombre' 
                                value={nombre}
                                onChange={(e) => setNombre(e.target.value)}
                                type='text'
                                className='form-control'
                                placeholder='Ingrese su nombre' />
                        </div>

                        <div className='form-group'>
                            <label className="col-sm-2 col-form-label" htmlFor="inpTel">Numero de telefono:</label>
                            <input 
                                required
                                id="inpTel" 
                                value={telefono}
                                onChange={(e) => setTelefono(e.target.value)}
                                type='number' 
                                className='form-control' 
                                placeholder='Ingrese su numero de telefono' />
                        </div>

                        <div className='form-group'>
                            <button type="submit" className="btn btn-info">Subir</button>
                        </div>
                    </div>
                </form>
            </section>
        </div>
    );
}
